// Access the incoming data from the first item
const inputData = items[0].json;

// 1. Get the array of images (located inside data.resultJson)
const images = inputData.data.resultJson || [];

// 2. Get the array of prompts (located at the root level)
const prompts = inputData.video_prompt || [];

// 3. Combine them by mapping over the images
// We return a new array of objects, where each object has a 'json' property
return images.map((imageUrl, index) => {
  return {
    json: {
      image_url: imageUrl,
      // Match the prompt by index. If the prompt list is shorter, use a fallback.
      video_prompt: prompts[index] || "No prompt available"
    }
  };
});

// 1. Get the Audio URL (assuming it's on the first item)
const audioUrl = $input.first().json.audioURL;

// 2. Get the list of Video URLs
// Note: Adjust 'resultJson' if your video links are stored differently
const videoList = $input.first().json.data.resultJson; 

// 3. Create the empty list for Creatomate elements
let videoElements = [];

// 4. Loop through your video list and format them for Creatomate
// We use a loop to dynamically add as many videos as you have
for (const videoUrl of videoList) {
  videoElements.push({
    "type": "video",
    "track": 1,          // track 1 ensures they play sequentially
    "source": videoUrl,  // The URL from your data
    "volume": "0%"       // Mute video to let Audio-JNM be the main sound
  });
}

// 5. Construct the final Payload for the API
const finalPayload = {
  "template_id": "3d990388-fd9f-42e3-a031-241deef254a3",
  "modifications": {
    "Audio-JNM.source": audioUrl,
    "Composition-C5Z.elements": videoElements
  }
};

// 6. Return the data so the next HTTP Request node can use it
return [{ json: finalPayload }];

const items = $input.all();
const allDone = items.every((item) => item?.json?.data?.state === "success");
const resultJsons = items.map((item) => item?.json?.data?.resultJson);
const taskIds = items.map((item) => item?.json?.data?.taskId);

const result = allDone
  ? { 
      allDone, 
      data: { 
