// n8n Code Node: Run Once for Each Item mode
const job = item.json;

// Budget
let budget = null;
if (job.project_budget_total) {
  budget = `$${job.project_budget_total}`;
} else if (job.project_budget_hourly_min && job.project_budget_hourly_max) {
  budget = `$${job.project_budget_hourly_min} - $${job.project_budget_hourly_max}/hr`;
} else if (job.project_budget_hourly_min) {
  budget = `$${job.project_budget_hourly_min}/hr`;
} else if (job.project_budget_hourly_max) {
  budget = `$${job.project_budget_hourly_max}/hr`;
} else {
  budget = "Not specified";
}

// Skills
const skills = Array.isArray(job.skills)
  ? job.skills.map(s => s.name).join(", ")
  : "None listed";

// Output
return {
  json: {
    title: job.title || "",
    description: job.description_text || "",
    budget,
    skills,
    client_location: job.client_city
      ? `${job.client_city}, ${job.client_country}`
      : job.client_country || "Unknown",
    url: job.url || ""
  }
};
